import React from "react";
import styled, { css } from 'styled-components';
import { NavLink as Link } from "react-router-dom";
import { FaAngleDown } from "react-icons/fa";
import {FaBars}  from "react-icons/fa";

import * as colors from "../../colors";
import Arrow from "../../images/arrow-icon.png";
import SearchWhite from "../../images/search-icon-white.png";

export default class SideNavBar extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      user: 'Wesley',
      menuOpen: false
    }
    this.openMenu = this.openMenu.bind(this)
  }

  openMenu() {
    const { menuOpen } = this.state
    const nav = document.querySelector('#nav')
    const bars = document.querySelector('.bars')
    const links = document.querySelector('#navlinks')
    if (!menuOpen) {
      nav.classList.add('expanded')
      bars.classList.add('expanded')
      links.style.display = 'flex'
      this.setState({ menuOpen: true })
    } else {
      nav.classList.remove('expanded')
      bars.classList.remove('expanded')
      links.style.display = 'none'
      this.setState({ menuOpen: false })
    }
  }

  render () {
    const { user, menuOpen } = this.state
    const { isMobile } = this.props

    return (
      <SideNavBarCont id='nav' isMobile={isMobile}>
      <a href='#' onClick={this.openMenu} className='bars'>
        <FontAwesomeIcon icon={!menuOpen ? faBars : faTimes} />
      </a>
        {/* Implement a hamburger icon slide in effect for small devices */}
        <SideNavMainLink className="menu_nav_link main_nav_link" to="/" activeClassName="active" exact>
          Soumya
          <NavIcon Arrow></NavIcon>
        </SideNavMainLink>
        <SideNavMainLink className="menu_nav_link" to="/discover" activeClassName="active">
          Discover
          <NavIcon SearchWhite></NavIcon>
        </SideNavMainLink>
        <SideNavHeader><HeaderText>Watched</HeaderText></SideNavHeader>
        <NavLink className="menu_nav_link" to="/watched/movies" activeClassName="active">Movies</NavLink>
        <NavLink className="menu_nav_link" to="/watched/tv-shows" activeClassName="active">TV Shows</NavLink>
        <SideNavHeader><HeaderText>Saved</HeaderText></SideNavHeader>
        <NavLink className="menu_nav_link" to="/saved/movies" activeClassName="active">Movies</NavLink>
        <NavLink className="menu_nav_link" to="/saved/tv-shows" activeClassName="active">TV Shows</NavLink>
      </SideNavBarCont>
    );
  }
}

const SideNavBarCont = styled.div`
  position: fixed;
  z-index: 9;
  width: 280px;
  height: 100%;
  background-color: ${colors.sideNavBar};
`

const SideNavMainLink = styled(Link)`
  position: relative;
  display: block;
  padding: 25px 35px;
  font-size: 1.6em;
  font-weight: 700;
  color: white;
`

const NavIcon = styled.div`
  position: absolute;
  right: 2.5rem;
  top: 50%;
`

const SideNavHeader = styled.div`
  display: block;
  padding: 15px 0px;
  font-size: 1.4em;
  font-weight: 300;
  color: white;
  border-bottom: 1px solid #aaa;
  margin-left: 35px;
`

const HeaderText = styled.div`

`

const NavLink = styled(Link)`
  display: block;
  padding: 15px 35px;
  font-size: 1.0em;
  font-weight: 100;
  color: white;
`
