import React, { useState, useEffect  } from 'react';
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import styled from 'styled-components';
import SideNavbar from "./components/sidenavbar"
import Hamburger from "./components/hamburger"

import Discover from "./pages/discover";

import './css/app.css'; 


export default class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      user: 'Soumya',
      isMobile: true,
      menuOpen: false
    }
    this.windowResize = this.windowResize.bind(this)
    this.openMenu = this.openMenu.bind(this)
  }

  componentDidMount() {
    window.addEventListener('resize', this.windowResize)
  }

  // eslint-disable-next-line react/sort-comp
  windowResize() {
    console.log("window");
    const width = document.documentElement.clientWidth
    const links = document.querySelector('#navbar')
    // eslint-disable-next-line no-unused-expressions
    if (width > 640) {
      links.style.display = 'block'
      this.setState({ isMobile: false })
      console.log("open menu");
      const { menuOpen, isMobile } = this.state
      const nav = document.querySelector('#navbar')
      if (!menuOpen) {
        nav.style.display = 'none'
        this.setState({ menuOpen: false })
      } else {
        nav.style.display = 'block'
        this.setState({ menuOpen: true })
      }
    } else {
      links.style.display = 'none'
      this.setState({ isMobile: true })
    }
  }

  render() {
  const { isMobile } = this.state
  
  return(
      <Router>
        <PageContainer>
          <a onClick={()=>this.windowResize()}>Click</a>
        <SideNavbar className='Navbar' isMobile={isMobile} 
          />
          <ContentWrapper>
            <Switch>
              <Route path="/discover" component={Discover} {...this.props} />
            </Switch>
          </ContentWrapper>
        </PageContainer>
      </Router>
  );
  }
}

const ContentWrapper = styled.main`
  @media (min-width: 769px) {
    padding-left: 280px;
  }
`;

const SideNavClose = styled.div`
  position: fixed;
  display: none;
  z-index: 0;
  background-color: rgba(0, 0, 0, 0.2);
  width: 100%;
  top: 0px;
  right: 0px;
  height: 100%;
  @media (max-width: 768px) {
    display: ${props => (props.activeSideBar ? "inherit" : "none")};
  }
`;

const PageContainer = styled.main``;
