import React, { useState, useEffect  } from 'react';
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import styled from 'styled-components';
import SideNavbar from "./components/sidenavbar"

import Discover from "./pages/discover";

import './css/app.css'; 

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      activeSideBar: false
    };
    this.openSideBar = this.openSideBar.bind(this);
    this.closeSideBar = this.closeSideBar.bind(this);
  }

  //Merge into toggle sidebar
  openSideBar(activeSideBar) {
    this.setState({
      activeSideBar: true
    });
  }

  closeSideBar(activeSideBar) {
    this.setState({
      activeSideBar: false
    });
  }

  render () {
    const { isMobile } = this.state

  return(
      
      <Router>
        <PageContainer>
        <SideNavClose
            activeSideBar={this.state.activeSideBar}
            onClick={this.closeSideBar}
          />
        <SideNavbar className='SideNavBarCont' isMobile={isMobile}  />
          <ContentWrapper>
            <Switch>
              <Route path="/discover" component={Discover} {...this.props}/>
            </Switch>
          </ContentWrapper>
        </PageContainer>
      </Router>
  );
  
}}



const ContentWrapper = styled.main`
  @media (min-width: 769px) {
    padding-left: 280px;
  }
`;


const PageContainer = styled.main``;
