import React from 'react';
import { NavLink as Link } from "react-router-dom";

import * as colors from "../../colors";
import Arrow from "../../images/arrow-icon.png";
import SearchWhite from "../../images/search-icon-white.png";
import { FaBars } from 'react-icons/fa';
import styled from 'styled-components';


export default class SideNavBar extends React.Component {
  render() {
    const { activeSideBar } = this.props;

    return (
      <>
      <Nav>
        <Bars />
      <SideNavBarCont activeSideBar={activeSideBar}>
        {/* Implement a hamburger icon slide in effect for mobile devices */}
        <SideNavMainLink
          className="menu_nav_link main_nav_link"
          to="/"
          activeClassName="active"
          exact
        >
          Alejandro
          <NavIcon src={Arrow} alt="arrow" />
        </SideNavMainLink>
        <SideNavMainLink
          className="menu_nav_link"
          to="/discover"
          activeClassName="active"
        >
          Discover
          <NavIcon src={SearchWhite} alt="search" />
        </SideNavMainLink>
        <SideNavHeader>
          <HeaderText>Watched</HeaderText>
        </SideNavHeader>
        <NavLink
          className="menu_nav_link"
          to="/watched/movies"
          activeClassName="active"
        >
          Movies
        </NavLink>
        <NavLink
          className="menu_nav_link"
          to="/watched/tv-shows"
          activeClassName="active"
        >
          TV Shows
        </NavLink>
        <SideNavHeader>
          <HeaderText>Saved</HeaderText>
        </SideNavHeader>
        <NavLink
          className="menu_nav_link"
          to="/saved/movies"
          activeClassName="active"
        >
          Movies
        </NavLink>
        <NavLink
          className="menu_nav_link"
          to="/saved/tv-shows"
          activeClassName="active"
        >
          TV Shows
        </NavLink>
      </SideNavBarCont>
      </Nav>
      </>
    );
  }
}
const SideNavBarCont = styled.div`
  background: #63D471;
  height: 85px;
  display: flex;
  justify-content: space-between;
  padding: 0.2rem calc((100vw - 1000px) / 2);
  z-index: 12;
  /* Third Nav */
  /* justify-content: flex-start; */
`;

const SideNavMainLink = styled(Link)`
position: relative;
display: flex;
background-color: ${(props) =>
  props.exact ? colors.sideNavBar : colors.primaryColor};
padding: 25px 35px;
font-size: 1.6em;
font-weight: 700;
color: white;
`;

const NavIcon = styled.img`
margin: auto 0 auto auto;
`;

const SideNavHeader = styled.div`
position: relative;
color: white;
padding: 35px 15px 15px 0px;
margin-left: 35px;
border-bottom: solid 1px ${colors.lightBackground};
`;

const HeaderText = styled.div`
display: block;
font-size: 1.6em;
font-weight: 600;
`;

  
const Bars = styled(FaBars)`
  display: none;
  color: #808080;
  @media screen and (max-width: 768px) {
    display: block;
    position: absolute;
    top: 0;
    right: 0;
    transform: translate(-100%, 75%);
    font-size: 1.8rem;
    cursor: pointer;
  }
`;
