import axios from 'axios';

// All of your API requests should be in this file

// declare the website url API taken from website 

const API = "801bc141deb8a0080cc3fedd1b47c2a3";
const url = "https://api.themoviedb.org"

//Get all trending movies
export const getAllMovies = async () => {
    try {
      const response = await axios.get(
        `{base_url}/3/trending/movie/week?api_key=${API}`
      );
      console.log("Succeeding fetching: " + getAllMovies.name);
      return response.data;
    } catch (error) {
      console.log("Error while fetching the movie:" + getAllMovies.name + "\n" + error);
    }
  };
  
  //Get movies by search term (name) and year
  export const getMovies = async (name, year) => {
    try {
      const response = await axios.get(
        `{url}/3/search/movie?api_key=${API}&query=${name}&year=${year}`
      );
      console.log("Succeeding fetching: " + getMovies.name);
      return response.data;
    } catch (error) {
      console.log("Error while fetching the movie: " + getMovies.name + "\n" + error);
    }
  };
  
  //Get list of genres:
  export const getGenresList = async () => {
    try {
      const response = await axios.get(
        `{url}}/3/genre/movie/list?api_key=${API}`
      );
      console.log("Succeeding fetching: " + getGenresList.name);
      return response.data.genres;
    } catch (error) {
      console.log("Error while fetching the movie: " + getGenresList.name + "\n" + error);
    }
  };
